import java.util.Hashtable;/** * Created by Hoshiko on 11/5/15. */public class Anagrams {    public static boolean String_anagram(String s1, String s2){        Hashtable<Character, Integer> hash = new Hashtable<Character,Integer>();        for(Character ch : s1.toCharArray()){            if(!Character.isAlphabetic(ch)) continue;            ch = Character.toLowerCase(ch);            if(hash.get(ch)==null)                hash.put(ch, 1);            else                hash.put(ch, hash.get(ch)+1);        }        for(Character ch : s2.toCharArray()){            if(!Character.isAlphabetic(ch)) continue;            ch = Character.toLowerCase(ch);            if(hash.get(ch)==null)                return false;            else if(hash.get(ch)==1)                hash.remove(ch);            else                hash.put(ch, hash.get(ch)-1);        }        return hash.isEmpty();    }    public static void main(String[] args) {        String s1 = "eleven plus two?";        String s2 = "TWELVE PLUS ONE!!";        System.out.println("is anagram?: " + String_anagram(s1, s2));        s1 = "eleven plus two?";        s2 = "TWELVE PLUS TWO!!";        System.out.println("is anagram?: " + String_anagram(s1, s2));    }}